mapper.select:
SELECT * FROM(
    SELECT @RN:=@RN+1 AS rnum, a.* FROM(
        SELECT * FROM board
        WHERE boardStatus = 0
        AND boardType = #{boardType}
        AND parent_boardIdx IS NULL

            AND boardTitle LIKE CONCAT('%', #{searchKeyword}, '%')

        ORDER BY boardIdx DESC
    ) a, (SELECT @RN:=0) b
) c WHERE c.rnum BETWEEN #{begin} AND #{end}

SELECT * FROM board
WHERE parent_boardIdx = #{boardIdx}
LIMIT 1

mapper.update:
UPDATE board
SET boardStatus = 1
WHERE boardIdx = #{boardIdx}

UPDATE board


    sub_boardType = #{subBoardType},
    boardTitle = #{boardTitle},
    boardContent = #{boardContent},
    boardStartRegDate = #{boardStartRegDate},
    boardEndRegDate = #{boardEndRegDate},

        file_name = #{fname},
        ori_name = #{oname}


        thumbnail_url = #{thumbfilename}


WHERE boardIdx = #{boardIdx}
