mapper.select:


SELECT * FROM(
SELECT @RN:=@RN+1 AS rnum, a.* FROM(
SELECT l.logIdx, l.logType, d.adminId, l.logTarget, l.logInfo, l.logPerValue, l.logCurValue, l.logDate FROM log l
inner join admin d ON l.adminIdx = d.adminIdx


        AND logDate BETWEEN #{datepicker} AND #{datepicker2}





                AND l.logTarget LIKE CONCAT ('%', #{search_keyword}, '%')


                AND l.logInfo LIKE CONCAT ('%', #{search_keyword}, '%')


                AND d.adminId LIKE CONCAT ('%', #{search_keyword}, '%')




ORDER BY l.logIdx DESC
) a, (SELECT @RN:=0) b
) c WHERE c.rnum BETWEEN #{begin} AND #{end}
