mapper.select:
SELECT COUNT(*)
FROM payment p

    p.userIdx = #{userIdx}

        AND p.paymentStatus = #{statusFilter}


        AND YEAR(p.paymentDate) = #{yearFilter}


        AND p.paymentType = #{typeFilter}

SELECT p.paymentType, p.paymentDate, p.orderId, p.paymentTransactionId AS paymentKey, p.paymentStatus, COALESCE(m.name, pr.prodName) AS itemTitle, COALESCE(m.poster, pr.prodImg) AS itemPosterUrl, t.tName AS theaterInfo, tt.startTime AS screenDate, pr.prodPrice FROM payment p LEFT JOIN reservation r ON p.reservIdx = r.reservIdx LEFT JOIN time_table tt ON r.timeTableIdx = tt.timeTableIdx LEFT JOIN movie m ON tt.mIdx = m.mIdx LEFT JOIN theater t ON r.tIdx = t.tIdx LEFT JOIN product pr ON p.prodIdx = pr.prodIdx
p.userIdx = #{userIdx}
AND p.paymentStatus = #{statusFilter}
AND YEAR(p.paymentDate) = #{yearFilter}
AND p.paymentType = #{typeFilter}
ORDER BY p.paymentDate DESC LIMIT #{begin}, #{end}
