mapper.select:
SELECT * FROM(
SELECT @RN:=@RN+1 AS rnum, a.* FROM(
SELECT * FROM nmember


        AND joinDate = #{datepicker}





                AND name LIKE CONCAT('%', #{search_keyword}, '%')


                AND email LIKE CONCAT('%', #{search_keyword}, '%')


                AND (name LIKE CONCAT('%', #{search_keyword}, '%') OR email LIKE CONCAT('%', #{search_keyword}, '%'))




ORDER BY nIdx DESC
) a, (SELECT @RN:=0) b
) c WHERE c.rnum BETWEEN #{begin} AND #{end}

SELECT * FROM web_movie.nmember
WHERE name = #{u_name}
    AND password = #{u_pw}
    AND birth = #{u_birth}
ORDER BY nIdx DESC LIMIT 1

SELECT * FROM nmember
WHERE name = #{name} AND phone = #{phone} AND birth = #{birth}
LIMIT 1
